<?phpinclude_once 'bookmethods.php';include_once "includes.php";GenerateTransactionSearch()->Render();global $wpdb;switch (page_action::GetAction()) {	case action_types::$search_transactions:		search_transactions()->Render();		break;    case action_types::$select_transaction:        select_transaction(selection::GetTransaction());        break;    case action_types::$delete_transaction:        break;};function select_transaction($transaction_post_id) {    ?>    <table id="formtable" width="100%" border="0" cellspacing="0" cellpadding="0">        <tr>            <td width="49%" valign="top">                <table id="formtable" border="0" cellspacing="0" cellpadding="0" style="margin: 10px 0 10px;">                    <tr>                        <form method="POST" action="" name="update_transaction" align="center">                            <td><?php echo get_transaction_id($transaction_post_id); ?></td>                            <td><?php echo get_transaction_customer_name($transaction_post_id); ?></td>                            <td><?php echo get_transaction_total($transaction_post_id); ?></td>                            <td width="auto" align="left">                                <input style="width: 90%; padding: 4px 3px 4px" type="date"                                       id="<?php echo request_change_transaction_date(); ?>"                                       name="<?php echo request_change_transaction_date(); ?>"                                       value="<?php echo get_transaction_date($transaction_post_id); ?>"/>                            </td>                            <td width="10%" align="right">                                <input name="<?php echo request_page_action() ?>" type="hidden" value="<?php echo update_transaction_action() ?>"/>                                <?php store_transaction_id() ?>                                <?php store_query_info() ?>                                <input type="submit" name="button" class="button-primary" value="Update"/>                            </td>                        </form>                        <td width="10%" align="right">                            <form method="POST" action="" name="back_to_query" align="center">                                <input name="<?php echo request_page_action() ?>" type="hidden" value="<?php echo query_search_action() ?>"/>                                <?php store_query_info() ?>                                <input type="submit" name="button" class="button-primary" value="Back to Search Results"/>                            </form>                        </td>                    </tr>                    <tr>                        <td><h4 style="margin: 10px 0px 0px 0px;">Book</h4></td>                        <td><h4 style="margin: 10px 0px 0px 0px;">ISBN</h4></td>                        <td><h4 style="margin: 10px 0px 0px 0px;">Barcode</h4></td>                        <td><h4 style="margin: 10px 0px 0px 0px;">Quantity Sold</h4></td>                        <td><h4 style="margin: 10px 0px 0px 0px;">Price</h4></td>                        <td></td>                    </tr>                    <tr>                        <td colspan="5">                            <hr/>                        </td>                    </tr>                    <?php                    $books = get_transaction_books($transaction_post_id);                    if (!empty($books)) {                        foreach ($books as $book) {                            book_display_transaction($book);                        }                    }                    ?>                </table>            </td>        </tr>    </table>    <?php}function book_display_transaction($book) {    $product_id = $book[0];    $soldqty = $book[1];    $productsoldprice = $book[2];    ?>    <tr>        <td><?php echo get_the_title($product_id); ?></td>        <td><?php echo get_book_sku($product_id); ?></td>        <td><?php echo get_book_barcode($product_id); ?></td>        <td><?php echo $soldqty; ?></td>        <td><?php $productsoldprice; ?></td>    </tr>    <?php}function GenerateTransactionSearch(){    return new TableArr(border(0).cellpadding(0).cellspacing(0).width(40),        new Row(            request_form_transactions()        )    );}?>